def calculatePrices(_pair, _token0_address, _token1_address):
    reserves = _pair.functions.getReserves().call()
    token0_decimals = getDecimals(_token0_address)
    token1_decimals = getDecimals(_token1_address)


    price01 = calculate_amount_out(reserves[0], reserves[1], 10**token0_decimals)
    price10 = calculate_amount_out(reserves[1], reserves[0], 10**token1_decimals)

    return (price01, price10)

def calculate_amount_out(_reserveIn, _reserveOut, _amountIn):
    amountInWithFee = 100*_amountIn * 997
    numerator = amountInWithFee*_reserveOut
    denominator = (_reserveIn * 1000) + amountInWithFee
    amountOut = numerator / denominator

    return amountOut



