from web3 import Web3
from library.producer import KafkaProducer
import time
import json

def getReserves(_pairAddress):
    pair = web3.eth.contract(address=_pairAddress, abi=pair_abi)

    (token0Reserves, token1Reserves, timestamp) = pair.functions.getReserves().call()

    return (token0Reserves, token1Reserves, timestamp)


def pushReserves(_exchange, _pairInfo, _token0Reserves, _token1Reserves, _timestamp):
    pairUpdate = {
            "pairAddress": _pairInfo['pairAddress'],
            "token0Symbol": _pairInfo['token0Symbol'],
            "token0Address": _pairInfo['token0Address'],
            "token0Reserves": _token0Reserves,
            "token0Decimals": _pairInfo['token0Decimals'],
            "token1Symbol": _pairInfo['token1Symbol'],
            "token1Address": _pairInfo['token1Address'],
            "token1Reserves": _token1Reserves,
            "token1Decimals": _pairInfo['token1Decimals'],
            "timestamp": _timestamp,
        }
    print(pairUpdate)
    producer.sendMessage(_exchange, json.dumps(pairUpdate))
    return

def getPairInfosFromJson(_path):
    file = open(_path)
    for line in file:
        return json.loads(line)




